// N8N Code Node - Email Content Parser for Pedal Boat Bookings
// This code extracts booking details from email content

// Get the email content from the input item - try multiple possible field names
const inputData = $input.all()[0].json;
const emailContent = inputData.textHtml || inputData.textPlain || inputData.body || inputData.text || inputData.content || inputData.html;

if (!emailContent) {
  console.log('Available input fields:', Object.keys(inputData));
  throw new Error('No email content found. Available fields: ' + Object.keys(inputData).join(', '));
}

// Log the email content for debugging
console.log('Email content found:', emailContent ? 'Yes' : 'No');
console.log('Content type:', typeof emailContent);
console.log('Content length:', emailContent ? emailContent.length : 0);

// Function to clean HTML and extract text
function cleanHtml(html) {
  if (!html) return '';

  // Remove HTML tags but preserve the content
  let text = html.replace(/<script[^>]*>.*?<\/script>/gi, '')
                .replace(/<style[^>]*>.*?<\/style>/gi, '')
                .replace(/<[^>]+>/g, ' ')
                .replace(/&nbsp;/g, ' ')
                .replace(/&amp;/g, '&')
                .replace(/&lt;/g, '<')
                .replace(/&gt;/g, '>')
                .replace(/&quot;/g, '"')
                .replace(/&#39;/g, "'")
                // Clean up UTF-8 encoding issues
                .replace(/Â/g, '')
                .replace(/À|Á|Ä|Ã|Å|à|á|ä|ã|å/g, 'a')
                .replace(/È|É|Ê|Ë|è|é|ê|ë/g, 'e')
                .replace(/Ì|Í|Î|Ï|ì|í|î|ï/g, 'i')
                .replace(/Ò|Ó|Ô|Ö|Õ|ò|ó|ô|ö|õ/g, 'o')
                .replace(/Ù|Ú|Û|Ü|ù|ú|û|ü/g, 'u')
                .replace(/\s+/g, ' ')
                .trim();

  return text;
}

// Convert HTML to clean text for easier parsing
const cleanText = cleanHtml(emailContent);
console.log('Clean text (first 500 chars):', cleanText.substring(0, 500));

// Function to extract field value using multiple patterns
function extractField(content, fieldName) {
  if (!content || typeof content !== 'string') {
    return '';
  }

  // Create a more precise boundary pattern that includes all possible next fields
  const nextFields = '(?=\\s*(?:Activity|Rate Option|Date|Time|Booking ID|Pax|Made By|Phone No|Email|Source|Payment Method|Notes|PromoCode|$))';

  // Try multiple patterns to match different formats
  const patterns = [
    // Pattern 1: "FieldName: Value" - capture until next field or end
    new RegExp(`${fieldName}:\\s*([^\\n\\r]+?)\\s*${nextFields}`, 'i'),

    // Pattern 2: "FieldName Value" (without colon) - for fields that might not have colon
    new RegExp(`${fieldName}\\s+([^\\n\\r]+?)\\s*${nextFields}`, 'i'),

    // Pattern 3: More flexible - capture until clear boundary
    new RegExp(`${fieldName}[:\\s]*([^\\n\\r]+?)\\s*(?=Made By:|Phone No:|Email:|Source:|Payment Method:|Notes:|PromoCode:|$)`, 'i')
  ];

  for (const pattern of patterns) {
    const match = content.match(pattern);
    if (match && match[1].trim()) {
      let value = match[1].trim();

      // Additional cleanup for specific fields
      if (fieldName === 'Rate Option') {
        value = value.replace(/\s+Rate$/, ''); // Remove trailing "Rate" if captured
      }

      return value;
    }
  }

  return '';
}

// Fixed function for extracting Pax
function extractPax(content) {
  console.log('Raw content around Pax:', content.substring(content.indexOf('Pax') - 20, content.indexOf('Pax') + 100));

  const patterns = [
    // Most specific pattern: look for number x description pattern after Pax
    /Pax[:\s]*(?:[A-Z]\s*)?(\d+\s*x\s*[^@\n\r]+?)(?=\s*@|\s*per person|Made By:|$)/i,
    // Look for "Pax" followed by content, but stop at "Made By" and preserve everything before the @ symbol
    /Pax[:\s]*([^@\n\r]+?)(?=\s*@)/i,
    // Alternative pattern that captures the full pax description until price
    /Pax[:\s]*(\d+\s*x\s*[^@\n\r]+?)(?=\s*@|\s*per person|Made By:|$)/i,
    // Fallback pattern for cases without @ symbol
    /Pax[:\s]*([^\\n\\r]+?)(?=\s*Made By:|$)/i,
    // Most basic pattern as last resort
    /Pax[:\s]*([^\\n\\r]+)/i
  ];

  for (const pattern of patterns) {
    const match = content.match(pattern);
    if (match && match[1].trim()) {
      let value = match[1].trim();
      console.log('Before cleanup:', `"${value}"`);

      // More aggressive cleanup for the stray characters
      value = value.replace(/\s*@\s*R?\d+[\.\d]*\s*$/i, '') // Remove @ R50.00 etc
                   .replace(/\s*per person.*$/i, '')        // Remove "per person" text
                   .replace(/\s*Af\d*\s*$/i, '')           // Remove partial "Af" if price got cut off
                   .replace(/^[A-Z]\s+/, '')               // Remove single letter at start (like "A ")
                   .replace(/^A\s*/, '')                   // More specific - remove "A" at start
                   .replace(/^\s*A\s+/, '')                // Remove "A" with any surrounding spaces
                   .trim();

      console.log('After cleanup:', `"${value}"`);

      // Don't remove "South African" or other descriptive text - only price-related content
      return value;
    }
  }
  return '';
}

// Special function for PromoCode (stop before any contact/signature info)
function extractPromoCode(content) {
  const patterns = [
    // Look for PromoCode but stop at any signature/contact info or newlines with contact details
    /PromoCode[:\s]*([^\\n\\r]+?)(?=\s*--|\s*Ken Walton|\s*Mobile|\s*Email|\s*Website|$)/i,
    // Try to capture just the promo code value more precisely
    /PromoCode[:\s]*([A-Za-z\s]+?)(?=\s*--|\s*Ken|\s*Mobile|$)/i,
    // Simple pattern as fallback
    /PromoCode[:\s]*([^\\n\\r]+)/i
  ];

  for (const pattern of patterns) {
    const match = content.match(pattern);
    if (match && match[1].trim()) {
      let value = match[1].trim();
      // Clean up any trailing contact info that might have been captured
      value = value.replace(/\s*--.*$/s, '')
                   .replace(/\s*Ken Walton.*$/s, '')
                   .replace(/\s*Mobile.*$/s, '')
                   .replace(/\s*Bi\s*$/i, 'Birthday'); // Fix truncated "Birthday"
      return value;
    }
  }
  return '';
}

// Extract all fields from the clean text
const extractedData = {
  activity: extractField(cleanText, 'Activity'),
  rateOption: extractField(cleanText, 'Rate Option'),
  date: extractField(cleanText, 'Date'),
  time: extractField(cleanText, 'Time'),
  bookingId: extractField(cleanText, 'Booking ID'),
  pax: extractPax(cleanText),
  madeBy: extractField(cleanText, 'Made By'),
  phoneNo: extractField(cleanText, 'Phone No'),
  email: extractField(cleanText, 'Email'),
  source: extractField(cleanText, 'Source'),
  paymentMethod: extractField(cleanText, 'Payment Method'),
  notes: extractField(cleanText, 'Notes'),
  promoCode: extractPromoCode(cleanText)
};

// Log each extraction for debugging
console.log('Extraction results:');
Object.keys(extractedData).forEach(key => {
  console.log(`${key}: "${extractedData[key]}"`);
});

// Add specific pax debugging
console.log('PAX DEBUGGING:');
console.log('Clean text around pax:', cleanText.substring(cleanText.toLowerCase().indexOf('pax') - 20, cleanText.toLowerCase().indexOf('pax') + 100));
const paxResult = extractPax(cleanText);
console.log('Final pax result from function:', `"${paxResult}"`);
console.log('Pax in extracted data:', `"${extractedData.pax}"`);

// Force the pax to be just a number if it contains a number
if (extractedData.pax && typeof extractedData.pax === 'string') {
  const forceNumberMatch = extractedData.pax.match(/(\d+)/);
  if (forceNumberMatch) {
    extractedData.pax = parseInt(forceNumberMatch[1], 10).toString();
    console.log('Forced pax to number:', `"${extractedData.pax}"`);
  }
}

// Additional cleanup - remove empty strings and set to null
Object.keys(extractedData).forEach(key => {
  if (extractedData[key] === '') {
    extractedData[key] = null;
  }
});

// Return the extracted data
return [
  {
    json: extractedData
  }
];
